pragma solidity ^0.5.8;
import "../library/SafeMath.sol";
contract MicroFinance {
    using SafeMath for uint256;
    struct debt {
        address lender;
        address borrower;
        uint256 amountOfDebt;
        uint256 interest;
    }
    mapping(bytes32 => debt) debtInfo;
    mapping(address => bytes32) requestDebt;
    mapping(address => bytes32) activeDebt;
    mapping(address => bytes32[]) debtHistory;
    mapping(address => mapping(address => bool)) borrowingStatus;
    mapping(address => address[]) debtorsList;
    mapping(address => uint256) userWallet;
    mapping(address => bool) isParticipated;
    address[] participants;
    event Request(address requester, uint256 amount, bytes32 requestID);
    event Lend(
        address lender,
        address debtor,
        uint256 amount,
        bytes32 requestID
    );
    event Pay(
        address lender,
        address debtor,
        uint256 amount,
        bytes32 requestID
    );
    function register() public {
        require(isParticipated[msg.sender] == false, "already registered");
        isParticipated[msg.sender] = true;
        participants.push(msg.sender);
    }
    function request(uint256 amount) public {
        bytes32 requestID = (sha256(abi.encodePacked(msg.sender, now)));
        require(isParticipated[msg.sender] == true, "have not registered");
        require(debtInfo[requestID].lender == address(0), "are already lended");
        require(amount > 100 wei, "the amount is too small to borrow");
        uint256 interestValue = (amount.mul(2)).div(100);
        debtInfo[requestID] = debt(
            address(0),
            msg.sender,
            amount,
            interestValue
        );
        requestDebt[msg.sender] = requestID;
        emit Request(msg.sender, amount, requestID);
    }
    function lend(bytes32 requestID) public payable {
        require(isParticipated[msg.sender] == true, "have not registered");
        uint256 amountOfDebt = debtInfo[requestID].amountOfDebt;
        address borrower = debtInfo[requestID].borrower;
        require(borrower != msg.sender, "cannot give loan by yourself");
        require(
            borrowingStatus[msg.sender][borrower] == false,
            "are already lended"
        );
        require(debtInfo[requestID].lender == address(0), "are already lended");
        require(msg.value >= amountOfDebt, "not enough money");
        debtInfo[requestID].lender = msg.sender;
        borrowingStatus[msg.sender][borrower] = true;
        requestDebt[borrower] = 0;
        activeDebt[borrower] = requestID;
        debtorsList[msg.sender].push(borrower);
        userWallet[borrower] = userWallet[borrower].add(msg.value);
        emit Lend(msg.sender, borrower, amountOfDebt, requestID);
    }
    function payBack() public payable {
        bytes32 loanID = activeDebt[msg.sender];
        require(loanID != 0, "do not have any debt");
        require(debtInfo[loanID].lender != address(0), "do not have any debt");
        uint256 valueNeedtoPay = getValueNeedToPay(loanID);
        require(msg.value >= valueNeedtoPay, "not enought money");
        address lender = debtInfo[loanID].lender;
        userWallet[lender] = userWallet[lender].add(msg.value);
        borrowingStatus[lender][msg.sender] = false;
        activeDebt[msg.sender] = 0;
        debtHistory[msg.sender].push(loanID);
        emit Pay(debtInfo[loanID].lender, msg.sender, valueNeedtoPay, loanID);
    }
    function withDraw(uint256 amount) public {
        uint256 amountWallet = userWallet[msg.sender];
        address payable receiver = msg.sender;
        require(amount != 0, "the amount can't be empty");
        require(amountWallet >= amount, "not enough money");
        userWallet[msg.sender] = userWallet[msg.sender].sub(amount);
        receiver.transfer(amount);
    }
    function getParticipants() public view returns (address[] memory) {
        return participants;
    }
    function checkParticipate() public view returns (bool) {
        return isParticipated[msg.sender];
    }
    function getRequestDebt(address to) public view returns (bytes32) {
        return requestDebt[to];
    }
    function getActiveDebt(address to) public view returns (bytes32) {
        return activeDebt[to];
    }
    function getDebtHistory(address to) public view returns (bytes32[] memory) {
        return debtHistory[to];
    }
    function checkBorrowingStatus(address borrower) public view returns (bool) {
        return borrowingStatus[msg.sender][borrower];
    }
    function getWalletValue() public view returns (uint256) {
        return userWallet[msg.sender];
    }
    function getDebtorsList() public view returns (address[] memory) {
        return debtorsList[msg.sender];
    }
    function getValueNeedToPay(bytes32 loanID) public view returns (uint256) {
        return debtInfo[loanID].amountOfDebt.add(debtInfo[loanID].interest);
    }
    function getLenderFromDebtInfo(bytes32 loanID)
        public
        view
        returns (address)
    {
        return debtInfo[loanID].lender;
    }
    function getDebtFromDebtInfo(bytes32 loanID) public view returns (uint256) {
        return debtInfo[loanID].amountOfDebt;
    }
    function getInterestFromDebtInfo(bytes32 loanID)
        public
        view
        returns (uint256)
    {
        return debtInfo[loanID].interest;
    }
}
